package com.ryk.vcsbyrfid.utils;

import com.github.sarxos.webcam.Webcam;
import com.google.gson.Gson;
import com.ryk.vcsbyrfid.model.entity.LicensePlate;
import com.ryk.vcsbyrfid.utils.HttpUtils;
import org.apache.http.HttpResponse;
import org.apache.http.util.EntityUtils;

import javax.imageio.ImageIO;
import java.awt.*;
import java.awt.image.BufferedImage;
import java.io.ByteArrayOutputStream;
import java.io.File;
import java.io.IOException;
import java.util.Base64;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

import static com.ryk.vcsbyrfid.constant.CommonConstant.APPCODE;

/**
 * 获取摄像头图片并识别车牌并返回
 */
public class LicensePlateRecognition {
    private static final int WIDTH = 640; // 拍照宽度
    private static final int HEIGHT = 480; // 拍照高度
//    private static final String CAMERA_NAME = "USB 2.0 CAMERA 0"; // 相机名称

    // 获取摄像头
    List<Webcam> webcams = Webcam.getWebcams();
    Webcam webcam;
    private static final String CAMERA_NAME = "HD Camera 0"; // 相机名称
    {
        webcam = webcams.get(0);
        Dimension[] viewSizes = webcam.getViewSizes();
        webcam.setViewSize(new Dimension(WIDTH, HEIGHT));
        // 打开摄像头
        webcam.open();

        //关闭摄像头
//        webcam.close();
    }

    /**
     * 获取摄像头当前图片
     * @return base64编码
     */
    private  String getPicToBase64() throws IOException {
        // 获取照片
        BufferedImage image = webcam.getImage();
        File output = new File("output.jpg"); // 文件名和路径
        ImageIO.write(image, "jpg", output); // 将图像保存到文件
        // 转换为Base64字符串
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        try {
            ImageIO.write(image, "JPEG", baos);
        } catch (IOException e) {
            e.printStackTrace();
        }
        byte[] bytes = baos.toByteArray();
        String base64 = Base64.getEncoder().encodeToString(bytes);
        return base64;
    }

    public  LicensePlate getLicense() throws IOException {
        String host = "https://api03.aliyun.venuscn.com";
        String path = "/ocr/car-license";
        String method = "POST";
        String appcode = APPCODE;
        Map<String, String> headers = new HashMap<String, String>();
        //最后在header中的格式(中间是英文空格)为Authorization:APPCODE 83359fd73fe94948385f570e3c139105
        headers.put("Authorization", "APPCODE " + appcode);
        //根据API的要求，定义相对应的Content-Type
        headers.put("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
        Map<String, String> querys = new HashMap<String, String>();
        Map<String, String> bodys = new HashMap<String, String>();

        String picToBase64 = getPicToBase64();
        bodys.put("pic", picToBase64);

//        bodys.put("pic", "");



        try {
            /**
             * 重要提示如下:
             * HttpUtils请从
             * https://github.com/aliyun/api-gateway-demo-sign-java/blob/master/src/main/java/com/aliyun/api/gateway/demo/util/HttpUtils.java
             * 下载
             *
             * 相应的依赖请参照
             * https://github.com/aliyun/api-gateway-demo-sign-java/blob/master/pom.xml
             */
            HttpResponse response = HttpUtils.doPost(host, path, method, headers, querys, bodys);
//            System.out.println(response.toString());
            //获取response的body
            String json = EntityUtils.toString(response.getEntity(), "UTF-8");
            Gson gson = new Gson();
            LicensePlate myResponse = gson.fromJson(json, LicensePlate.class);

            System.out.println("车牌信息:"+myResponse.getData().getNumber() + " " + myResponse.getData().getColor());
            return myResponse;
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }

    }
}
